document.addEventListener('DOMContentLoaded', () => {
    const chatContainer = document.getElementById('chat-container');
    const messageInput = document.getElementById('message-input');
    const sendButton = document.getElementById('send-btn');
    const resetButton = document.getElementById('reset-btn');
    const typingIndicator = document.getElementById('typing-indicator');

    messageInput.addEventListener('input', function() {
        this.style.height = 'auto';
        this.style.height = this.scrollHeight + 'px';
    });

    sendButton.addEventListener('click', sendMessage);
    messageInput.addEventListener('keydown', (e) => {
        if (e.key === 'Enter' && !e.shiftKey) {
            e.preventDefault();
            sendMessage();
        }
    });

    resetButton.addEventListener('click', resetChat);

    async function sendMessage() {
        const message = messageInput.value.trim();
        if (!message) return;

        addMessage('user', message);
        messageInput.value = '';
        messageInput.style.height = 'auto';

        typingIndicator.style.display = 'block';
        chatContainer.scrollTop = chatContainer.scrollHeight;

        try {
            const response = await fetch('/cloudchat/deepseek_chat', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Referer': 'https://www.rendazhang.com'
                },
                body: JSON.stringify({ message })
            });

            if (!response.ok) {
                throw new Error(`Request failed: ${response.status}`);
            }

            typingIndicator.style.display = 'none';

            const aiMessageContainer = document.createElement('div');
            aiMessageContainer.className = 'message ai-message';
            chatContainer.appendChild(aiMessageContainer);

            const reader = response.body.getReader();
            const decoder = new TextDecoder();
            let aiMessage = '';

            while (true) {
                const { value, done } = await reader.read();
                if (done) break;

                const chunk = decoder.decode(value, { stream: true });
                const lines = chunk.split('\n').filter(line => line.trim());

                for (const line of lines) {
                    try {
                        const data = JSON.parse(line);
                        if (data.text) {
                            aiMessage += data.text;
                            aiMessageContainer.textContent = aiMessage;
                            chatContainer.scrollTop = chatContainer.scrollHeight;
                        }
                    } catch (e) {
                        console.error('Parse error:', e);
                    }
                }
            }
        } catch (error) {
            typingIndicator.style.display = 'none';
            addMessage('ai', `Error: ${error.message}`);
            console.error('API error:', error);
        }
    }

    async function resetChat() {
        if (confirm('确定要重置会话吗？这将清除所有对话历史。')) {
            try {
                const response = await fetch('/cloudchat/reset_chat', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                        'Referer': 'https://www.rendazhang.com'
                    },
                    body: JSON.stringify({})
                });

                if (!response.ok) {
                    throw new Error(`Reset failed: ${response.status}`);
                }

                chatContainer.innerHTML = '<div class="info-text">会话已重置，请输入新消息</div>';
            } catch (error) {
                alert(`重置会话失败: ${error.message}`);
                console.error('Reset error:', error);
            }
        }
    }

    function addMessage(role, content) {
        const messageElement = document.createElement('div');
        messageElement.className = `message ${role}-message`;
        messageElement.textContent = content;
        chatContainer.appendChild(messageElement);
        chatContainer.scrollTop = chatContainer.scrollHeight;
    }
});
