document.addEventListener('DOMContentLoaded', () => {
    const chatContainer = document.getElementById('chat-container');
    const messageInput = document.getElementById('message-input');
    const sendButton = document.getElementById('send-btn');
    const resetButton = document.getElementById('reset-btn');
    const typingIndicator = document.getElementById('typing-indicator');
    const loadingIndicator = document.getElementById('loading-indicator');

    window.librariesLoaded = false;

    // Show loading state initially
    loadingIndicator.style.display = 'block';
    messageInput.disabled = true;
    
    // 移除mermaid初始化（现在在HTML中处理）
    
    const STORAGE_KEY = "deepseek_chat_history";
    const MAX_TOKENS = 15000;
    const AVG_WORD_LENGTH = 4;
    const AVG_TOKENS_PER_WORD = 1.5;

    let conversationHistory = [];
    try {
        const stored = localStorage.getItem(STORAGE_KEY);
        conversationHistory = stored ? JSON.parse(stored) : [];
    } catch (e) {
        conversationHistory = [];
    }

    function countTokens(msg) {
        return Math.ceil((msg.content.length / AVG_WORD_LENGTH) * AVG_TOKENS_PER_WORD);
    }

    function trimHistory() {
        let tokens = conversationHistory.reduce((t, m) => t + countTokens(m), 0);
        while (tokens > MAX_TOKENS && conversationHistory.length > 0) {
            conversationHistory.shift();
            tokens = conversationHistory.reduce((t, m) => t + countTokens(m), 0);
        }
    }

    function saveHistory() {
        try {
            localStorage.setItem(STORAGE_KEY, JSON.stringify(conversationHistory));
        } catch (e) {}
    }

    function createCopyButton(textProvider) {
        const btn = document.createElement('button');
        btn.className = 'copy-btn';
        btn.textContent = '复制';
        btn.style.display = 'none';
        btn.addEventListener('click', (e) => {
            e.stopPropagation();
            const text = typeof textProvider === 'function' ? textProvider() : textProvider;
            navigator.clipboard.writeText(text).then(() => {
                btn.textContent = '已复制';
                setTimeout(() => {
                    btn.textContent = '复制';
                    btn.style.display = 'none';
                }, 1000);
            });
        });
        return btn;
    }

    function attachCopyInteraction(container, btn) {
        container.addEventListener('mouseenter', () => {
            btn.style.display = 'inline-block';
        });
        container.addEventListener('mouseleave', () => {
            if (btn.textContent === '复制') {
                btn.style.display = 'none';
            }
        });
        container.addEventListener('click', () => {
            btn.style.display = 'inline-block';
        });
    }

    function loadScript(src) {
        return new Promise((resolve, reject) => {
            const script = document.createElement('script');
            script.src = src;
            script.onload = resolve;
            script.onerror = reject;
            document.head.appendChild(script);
        });
    }

    function loadEnhancementLibraries() {
        const enhancementProgress = document.createElement('div');
        enhancementProgress.id = 'enhancement-progress';
        enhancementProgress.innerHTML = `
            <div class="pulse-container">
              <div class="pulse-dot pulse-dot-1"></div>
              <div class="pulse-dot pulse-dot-2"></div>
              <div class="pulse-dot pulse-dot-3"></div>
            </div>
            <p>正在优化阅读体验...</p>`;
        document.body.appendChild(enhancementProgress);

        Promise.all([
            loadScript('js/highlight.min.js'),
            loadScript('js/mermaid.min.js')
        ]).then(() => {
            if (window.mermaid) {
                mermaid.initialize({ startOnLoad: false });
            }
            window.librariesLoaded = true;
            document.querySelectorAll('.ai-message').forEach(msg => {
                const c = msg.querySelector('div');
                if (c) {
                    applyEnhancements(c);
                }
            });
            enhancementProgress.style.opacity = '0';
            setTimeout(() => {
                document.body.removeChild(enhancementProgress);
            }, 300);
        }).catch(error => {
            console.error('美化资源加载失败:', error);
            enhancementProgress.innerHTML = '<p>优化功能加载失败，基础功能不受影响</p>';
            setTimeout(() => {
                document.body.removeChild(enhancementProgress);
            }, 2000);
        });
    }

    messageInput.addEventListener('input', function() {
        this.style.height = 'auto';
        this.style.height = this.scrollHeight + 'px';
    });

    sendButton.addEventListener('click', sendMessage);
    messageInput.addEventListener('keydown', (e) => {
        if (e.key === 'Enter' && !e.shiftKey) {
            e.preventDefault();
            sendMessage();
        }
    });

    resetButton.addEventListener('click', resetChat);
    
    // 检查元素是否在可视区域
    function isElementInViewport(el) {
        const rect = el.getBoundingClientRect();
        return (
            rect.top >= 0 &&
            rect.left >= 0 &&
            rect.bottom <= (window.innerHeight || document.documentElement.clientHeight) &&
            rect.right <= (window.innerWidth || document.documentElement.clientWidth)
        );
    }

    // 应用代码高亮和图表渲染，优先处理可视区域
    function applyEnhancements(container) {
        if (!container) return;

        if (window.hljs) {
            container.querySelectorAll('pre code').forEach(block => {
                if (isElementInViewport(block)) {
                    hljs.highlightElement(block);
                }
            });
        }

        if (window.mermaid) {
            requestIdleCallback(() => {
                container.querySelectorAll('.language-mermaid').forEach(diagram => {
                    mermaid.init(undefined, diagram);
                });
            });
        }
    }

    // 滚动时检查并美化可视区域
    let scrollTimer;
    window.addEventListener('scroll', () => {
        clearTimeout(scrollTimer);
        scrollTimer = setTimeout(() => {
            document.querySelectorAll('.ai-message div').forEach(c => {
                if (isElementInViewport(c)) {
                    applyEnhancements(c);
                }
            });
        }, 100);
    });

    // 添加消息函数
    function addMessage(role, content, enhance = true) {
        const messageElement = document.createElement('div');
        messageElement.className = `message ${role}-message` + (role === 'ai' ? ' markdown-body' : '');
        
        if (role === 'ai') {
            messageElement.style.position = 'relative';
            const copyBtn = createCopyButton(content);
            messageElement.appendChild(copyBtn);
            attachCopyInteraction(messageElement, copyBtn);
            
            const contentDiv = document.createElement('div');
            // 仅使用marked解析，不立即应用高亮
            contentDiv.innerHTML = DOMPurify.sanitize(marked.parse(content));
            
            if (enhance && window.librariesLoaded) {
                applyEnhancements(contentDiv);
            }
            
            messageElement.appendChild(contentDiv);
        } else {
            messageElement.textContent = content;
        }
        
        chatContainer.appendChild(messageElement);
        chatContainer.scrollTop = chatContainer.scrollHeight;
        return messageElement;
    }

    // 初始化历史消息
    if (conversationHistory.length > 0) {
        chatContainer.innerHTML = "";
        conversationHistory.forEach(msg => {
            const role = msg.role === "assistant" ? "ai" : "user";
            addMessage(role, msg.content, false);
        });
    }

    // History loaded - hide loading indicator and enable input
    loadingIndicator.style.display = 'none';
    messageInput.disabled = false;
    messageInput.placeholder = '输入消息...';

    setTimeout(loadEnhancementLibraries, 0);

    async function sendMessage() {
        const message = messageInput.value.trim();
        if (!message) return;

        addMessage('user', message);
        conversationHistory.push({ role: "user", content: message });
        trimHistory();
        saveHistory();
        messageInput.value = '';
        messageInput.style.height = 'auto';

        typingIndicator.style.display = 'block';
        chatContainer.scrollTop = chatContainer.scrollHeight;

        try {
            const response = await fetch('/cloudchat/deepseek_chat', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Referer': 'https://www.rendazhang.com'
                },
                body: JSON.stringify({ message })
            });

            if (!response.ok) {
                throw new Error(`Request failed: ${response.status}`);
            }

            typingIndicator.style.display = 'none';

            const aiMessageContainer = document.createElement('div');
            aiMessageContainer.className = 'message ai-message markdown-body';
            const contentDiv = document.createElement('div');
            aiMessageContainer.appendChild(contentDiv);
            chatContainer.appendChild(aiMessageContainer);

            const copyBtn = createCopyButton(() => contentDiv.textContent);
            aiMessageContainer.appendChild(copyBtn);
            attachCopyInteraction(aiMessageContainer, copyBtn);

            const reader = response.body.getReader();
            const decoder = new TextDecoder();
            let aiMessage = '';

            while (true) {
                const { value, done } = await reader.read();
                if (done) break;

                const chunk = decoder.decode(value, { stream: true });
                const lines = chunk.split('\n').filter(line => line.trim());

                for (const line of lines) {
                    try {
                        const data = JSON.parse(line);
                        if (data.text) {
                            aiMessage += data.text;
                            // 仅更新内容，不立即应用高亮
                            contentDiv.innerHTML = DOMPurify.sanitize(marked.parse(aiMessage));
                            
                            // 如果库已加载，应用增强
                            if (window.librariesLoaded) {
                                applyEnhancements(contentDiv);
                            }
                            
                            chatContainer.scrollTop = chatContainer.scrollHeight;
                        }
                    } catch (e) {
                        console.error('Parse error:', e);
                    }
                }
            }
            
            conversationHistory.push({ role: "assistant", content: aiMessage });
            trimHistory();
            saveHistory();
        } catch (error) {
            typingIndicator.style.display = 'none';
            addMessage('ai', `Error: ${error.message}`);
            console.error('API error:', error);
        }
    }

    async function resetChat() {
        if (confirm('确定要重置会话吗？这将清除所有对话历史。')) {
            try {
                const response = await fetch('/cloudchat/reset_chat', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                        'Referer': 'https://www.rendazhang.com'
                    },
                    body: JSON.stringify({})
                });

                if (!response.ok) {
                    throw new Error(`Reset failed: ${response.status}`);
                }

                chatContainer.innerHTML = '<div class="info-text">会话已重置，请输入新消息</div>';
                conversationHistory = [];
                saveHistory();
            } catch (error) {
                alert(`重置会话失败: ${error.message}`);
                console.error('Reset error:', error);
            }
        }
    }
    
});
